package helloworld.DAY2;

public class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=3,b=30;
		boolean z;
		z = a==b;
		System.out.println("a==b is "+z);
		
		z=a!=b;
		System.out.println("a!=b is "+z);
        
		z=a>b;
		System.out.println("a>b is "+z);
		
		z=a<b;
		System.out.println("a<b is "+z);
		
		z=a<=b;
		System.out.println("a<=b is "+z); //annyone condition should be true either < less than or = equals to
		
		z=a>=b;
		System.out.println("a>=b is "+z);
	}

}
